//search
var itemList = Things["OLY.ErrCode.ST"].QueryStreamData({
	oldestFirst: undefined /* BOOLEAN */,
	maxItems: undefined /* NUMBER */,
	sourceTags: undefined /* TAGS */,
	endDate: undefined /* DATETIME */,
	query: undefined /* QUERY */,
	source: undefined /* STRING */,
	startDate: undefined /* DATETIME */,
	tags: undefined /* TAGS */
});
var itemListView = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "OLY.ErrCodeView.DS"
});
itemListView.AddField({name : "ErrCode", baseType : "STRING"});
itemListView.AddField({name : "CNT", baseType : "INTEGER", aspects : {defaultValue:1}});
for each(var row in itemList.rows) {
    var obj = new Object();
    //obj.DateStr = (row["timestamp"].getFullYear()) + "年" + (row["timestamp"].getMonth()+1) + "月" + row["timestamp"].getDate() + "日";
    obj.yyyymmdd = new Date(row["timestamp"].getFullYear(),(row["timestamp"].getMonth()+1), row["timestamp"].getDate(),0,0,0);
    obj.ErrCode = row.err_code;
    obj.CNT = 1;
    itemListView.AddRow(obj);
}

////Aggregate
var sumItemList = Resources["InfoTableFunctions"].Aggregate({
    t: itemListView,
    columns: "CNT",
    aggregates: "SUM",
    groupByColumns: "yyyymmdd,ErrCode"
});

//Pivot 
var result = Resources["InfoTableFunctions"].Pivot({
    t: sumItemList ,
    nameColumn: "ErrCode" ,
    valueColumn: "SUM_CNT" ,
    timestampColumn: "yyyymmdd" 
});

result.AddField({name : "DateStr", baseType : "STRING"});

for each (var row in result.rows) {
    row.DateStr = (row["timestamp"].getFullYear()) + "年" + (row["timestamp"].getMonth()+1) + "月" + row["timestamp"].getDate() + "日";
}

result.RemoveField("timestamp");
