function compare(a, b ){
    var r = 0;
    if(a.Number > b.Number){
        r = -1;
    }
    else if(a.Number < b.Number){
        r = 1;
    }
}

function getpartinfo(jsonbom, hierarchy, path, parent) {
    var partid;
    if ("PartId" in jsonbom) {
        partid = jsonbom["PartId"];
    }
    var components;
    if ("Components" in jsonbom) {
        components = jsonbom["Components"];
    }
    if ("PartNumber" in jsonbom) {
        var part = new Object();
        var partNumber = jsonbom["PartNumber"];
        var hasChildren = false;
        if (components !== undefined && components.length > 0) {
            hasChildren = true;
        }
        part["Number"] = partNumber;
        part["Name"] = jsonbom["PartName"];
        part["Version"] = "";
        part["PartID"] = partid;
        if(jsonbom["Part"]!=undefined){
        	part["Version"] = jsonbom["Part"]["Version"];
        }
        part["ParentId"] = path;
        part["Id"] = path + "/" + partNumber;
        part["ParentChildId"] = "";
        part["PVTreeId"] = jsonbom["PVTreeId"] ;
        if (hierarchy > 1) {
            part["ParentChildId"] = parent + "/" + partNumber;
        }
        if ("PartUse" in jsonbom) {
            var partUse = jsonbom["PartUse"];
            if ("Quantity" in partUse) {
                part["Quantity"] = partUse.Quantity;
            }
            if ("LineNumber" in partUse) {
                part["LineNumber"] = partUse.LineNumber;
            }
        }
        part["HasChildren"] = hasChildren;
        partstructure.push(part);
    }
    if (components !== undefined) {
        components.sort(compare);
        for(var i=0; i< components.length; i++) {
            getpartinfo(components[i], hierarchy + 1, path + "/" + jsonbom["PartNumber"], jsonbom["PartNumber"]);
        }
    }
    return "";
}


partstructure.forEach(function(e)  {
    result.AddRow({
        CostFlag: true,
        Number: e.Number,
        //PartId: e.PartID,
        Name: e.Name,
        HasChildren: e.HasChildren,
        ParentId: e.ParentId,
        Id: e.Id
    });
});

