let result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "MME.COMM.POC.MV.DS"
});

if (EntityName) {
	let props = Things[EntityName].GetPropertyValues();
    if (props.getRowCount()>0) {
        var mode = JSON.parse(me.ModeType)[props.Mode];
        var state = JSON.parse(me.OperationState)[props.Operation_State];
        var mainOpTime = "0時間0分", hour = 0, min = 0, runTime = 0;
        if (props.StartProgram) {
            var main = props.StartProgram;
            hour = Math.floor(main / (60*60*1000));
            min = Math.round((main-hour*60*60*1000) / (60*1000));
            mainOpTime = hour + "時間" + min + "分";
            //runTime = hour + Number((min/60).toFixed(1));
        }
        var typeSpec = "", targetTime = 22, targetTimeStr = "22時間0分", diffTimeStr = "22時間0分";
        var targetInfo = me.GetTargetInfoByMainProgram({
            DeviceName: props.DeviceName /* STRING */,
            MainProgram: props.Main_Program /* INTEGER */
        });
        if (targetInfo.getRowCount()>0) {
            typeSpec = targetInfo.DeviceType + " " + targetInfo.DeviceSpec;
            targetTime = targetInfo.TargetTime;
            //var diffTime = targetTime*(60*60*1000) - props.StartProgram;
            var targetTimeArr = targetTime.toString().split(".");
            //var diffTimeArr = diffTime.toString().split(".");
            var targetMin = ((targetTime-new Number(targetTimeArr[0]))*60).toFixed(0);
            targetTimeStr = targetTimeArr[0] + "時間" + targetMin + "分";
            var diffHour = 0, diffMin = 0;
            if (targetMin < min) {            	
                diffHour = targetTimeArr[0] - hour - 1;
                diffMin = new Number(targetMin) + 60 -min;
            } else {
                diffHour = targetTimeArr[0] - hour;
                diffMin = targetMin -min;
            }
            diffTimeStr = diffHour + "時間" + diffMin + "分";
        }

        // MME.COMM.POC.MV.DS entry object
        let newEntry = {
            Alarm: props.Alarm, // INTEGER
            ap_B_axis: props.ap_B_axis, // NUMBER
            ap_C_axis: props.ap_C_axis, // NUMBER
            ap_X_axis: props.ap_X_axis, // NUMBER
            ap_Y_axis: props.ap_Y_axis, // NUMBER
            ap_Z_axis: props.ap_Z_axis, // NUMBER
            being_executed_program: props.being_executed_program, // INTEGER
            Feed: props.Feed, // INTEGER
            Main_Program: props.Main_Program, // INTEGER
            Mode: mode, // STRING
            Operation_State: state, // STRING
            Path: props.Path, // INTEGER
            Spindle: props.Spindle, // INTEGER
            Spindle_load_meter: props.Spindle_load_meter, // INTEGER
            Time_Cutting: props.Time_Cutting, // NUMBER
            Time_Cycle: props.Time_Cycle, // NUMBER
            MainOpTime: mainOpTime,
            DeviceTypeSpec: typeSpec,
            TargetTime: targetTimeStr,
            //RunTime: runTime,
            DiffTime: diffTimeStr,
            RunRatio: (runTime/targetTime*100).toFixed(0),
            DeviceName: props.DeviceName // STRING
        };
        result.AddRow(newEntry);
    }
}

