let alarm = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "MME.COMM.POC.DashboardData.DS"
});

try {
    var propDefs = me.GetPropertyDefinitions({
        category: undefined /* STRING */,
        type: undefined /* BASETYPENAME */,
        dataShape: undefined /* DATASHAPENAME */
    });
    propDefs.RemoveAllRows();
    propDefs.AddRow({ "name" : "Main_Program" });
    propDefs.AddRow({ "name" : "Operation_State" });
    propDefs.AddRow({ "name" : "Alarm" });
    var vs = Things[EntityName].QueryNamedPropertyHistory({
        oldestFirst: true /* BOOLEAN */,
        maxItems: undefined /* NUMBER {"defaultValue":500} */,
        endDate: toDate /* DATETIME */,
        propertyNames: propDefs /* INFOTABLE {"dataShape":"EntityList"} */,
        query: undefined /* QUERY */,
        startDate: fromDate /* DATETIME */
    });
    var mainPG, opState="", alarm1="", deviceName = Things[EntityName].DeviceName, cnt = vs.getRowCount(), start, end;
    var query = {
       "filters":{
          "type": "EQ",
          "fieldName": "DeviceName",
          "value": deviceName
       }
    }; 
    let infos = Things["MME.COMM.POC.Program.DT"].QueryDataTableEntries({
        maxItems: undefined /* NUMBER {"defaultValue":500} */,
        values: undefined /* INFOTABLE */,
        query: query /* QUERY */,
        source: undefined /* STRING */,
        tags: undefined /* TAGS */
    });
    for (var i=0; i<cnt; i++) {
        mainPG = vs[i].Main_Program;
        opState = (vs[i].Operation_State || "");
        alarm1 = (vs[i].Alarm || "");
        end = vs[i].timestamp;
        if (i == 0) {
            start = fromDate;
            var prev = Things[EntityName].QueryNamedPropertyHistory({
                maxItems: 1 /* NUMBER {"defaultValue":500} */,
                endDate: fromDate /* DATETIME */,
                propertyNames: propDefs /* INFOTABLE {"dataShape":"EntityList"} */
            });
            if (prev) {
                mainPG = prev.Main_Program;
                opState = (prev.Operation_State || "");
                alarm1 = (prev.Alarm || "");
            }
        } else if (i == (cnt-1)) {
            start = vs[i].timestamp;
            end = toDate;           
            var next = Things[EntityName].QueryNamedPropertyHistory({
                maxItems: 1 /* NUMBER {"defaultValue":500} */,
        		startDate: toDate /* DATETIME */,
                propertyNames: propDefs /* INFOTABLE {"dataShape":"EntityList"} */
            });
            if (next) {
                mainPG = next.Main_Program;
                opState = (next.Operation_State || "");
                alarm1 = (next.Alarm || "");
            }
        } else {
            mainPG = vs[i-1].Main_Program;
            opState = (vs[i-1].Operation_State || "");
            alarm1 = (vs[i-1].Alarm || "");
            start = vs[i-1].timestamp;
        }      
        if (mainPG > 0 && mainPG !== vs[i].Main_Program) {
            var queryP = {
               "filters":{
                  "type": "EQ",
                  "fieldName": "MainProgram",
                  "value": mainPG
               }
            };
            let info = Resources["InfoTableFunctions"].Query({
                t: infos /* INFOTABLE */,
                query: queryP /* QUERY */
            });
			var type = "XXX", spec = "XXX", target = "22";
            if (info && info.getRowCount()) {
                type = info.DeviceType;
                spec = info.DeviceSpec;
                target = info.TargetTime;
            }
            program.AddRow({"reason": mainPG, "info": type + " " + spec + " : " + target,
                          "start": start, "end": end, "color": ""});
        }
        if (opState != "" && opState != vs[i].Operation_State) {
            operation.AddRow({"reason": opState, "info": JSON.parse(me.OperationState)[opState],
                          "start": start, "end": end, "color": ""});
        }
        if (alarm1 != "" && alarm1 != vs[i].Alarm) {
            alarm.AddRow({"reason": alarm1, "info": JSON.parse(me.OperationState)[opState],
                          "start": start, "end": end, "color": ""});
        }
    }
    result.AddRow({Resource: "メインプログラム", Data: program});
    result.AddRow({Resource: "稼働状況", Data: operation});
    result.AddRow({Resource: "アラーム", Data: alarm});
} catch(err) {
    logger.error("GetMainDashboardList : " + err);
}

