var result = "0時間0分0秒", main, hour, min, sec;
if (EntityName) {
    var device = Things[EntityName];
    //logger.warn(device.name + " Operation_State:" + device.Operation_State);
    var now = new Date();
    if (device.Operation_State == 3) {
        if (device.StartOperation) {
            device.Maintenance = device.Maintenance - (now.getTime()-device.StartOperation.getTime());
            main = device.Maintenance;
            hour = Math.floor(main / (60*60*1000));
            min = Math.floor((main-hour*60*60*1000) / (60*1000));
            sec = Math.floor((main-hour*60*60*1000-min*60*1000) / (1000));
            result = hour + "時間" + min + "分" + sec + "秒";
        }
    } else {
        main = device.Maintenance;
        hour = Math.floor(main / (60*60*1000));
        min = Math.floor((main-hour*60*60*1000) / (60*1000));
        sec = Math.floor((main-hour*60*60*1000-min*60*1000) / (1000));
        result = hour + "時間" + min + "分" + sec + "秒";
    }
    device.StartOperation = now;
}

