Row.sql:
SELECT to_char(time, 'yyyymmddhhmm') as time_str, property_value
 FROM public."value_stream" where source_id=[[Source]] and property_name='being_executed_program' and time>=[[startdate]] and time<=[[enddate]]
 group by time_str, property_value;

ALTER TABLE IF EXISTS public."MainHistory"
    ADD COLUMN IF NOT EXISTS "DeviceType" character varying;
ALTER TABLE IF EXISTS public."MainHistory"
    ADD COLUMN IF NOT EXISTS "DeviceSpec" character varying;

-- Table: public.MainHistory

-- DROP TABLE IF EXISTS public."MainHistory";

CREATE TABLE IF NOT EXISTS public."MainHistory"
(
    "Name" character varying COLLATE pg_catalog."default" NOT NULL,
    "Main_Program" integer,
    "being_executed_program" integer,
    "Operation_Time" double precision,
    "Regist_Date" timestamp without time zone
)
WITH (
    OIDS = FALSE
)
TABLESPACE pg_default;

ALTER TABLE IF EXISTS public."MainHistory"
    OWNER to twadmin;

