var result = 0;
try {
    var paramsCSV = {
        path: "error_master.csv" /* STRING */,
        columnMappings: "MOTOR_LINE;UNIT_CODE;ERROR_CODE;ERROR_COMENT;ERROR_NOTE;ERROR_KBN;UP_DATE;MAKE_DATE" /* STRING */,
        hasHeader: true /* BOOLEAN */,
        longitudeField: undefined /* NUMBER */,
        dateFormat: undefined /* STRING */,
        fileRepository: "MEL.CSV.Repo" /* THINGNAME */,
        latitudeField: undefined /* NUMBER */,
        fieldDelimiter: "," /* STRING */,
        stringDelimiter: undefined /* STRING */,
        content: "SA Data Input\n" /* STRING */,
        dataShape: "MEL.AR.ErrorMaster.DS" /* DATASHAPENAME */
    };

    // result: INFOTABLE
    var dataList = Resources["CSVParserFunctions"].ReadCSVFile(paramsCSV);
    var params = {
        infoTableName : "InfoTable",
        dataShapeName : "MEL.AR.ErrorMaster.DS"
    };
    var infoTable = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape(params);   

    // add rows to Stream
    for (result=0; result<dataList.length; result++) {
        var row = dataList[result];
        var values = {};
        values.MOTOR_LINE=row.MOTOR_LINE;
        values.UNIT_CODE=row.UNIT_CODE;
        values.ERROR_CODE=row.ERROR_CODE;
        values.ERROR_COMENT=row.ERROR_COMENT;
        values.ERROR_NOTE=row.ERROR_NOTE;
        values.ERROR_KBN=row.ERROR_KBN;
        values.UP_DATE=row.UP_DATE;
        values.MAKE_DATE=row.MAKE_DATE;
		infoTable.AddRow(values);
    }

    Things["MEL.AR.ErrorMaster.DT"].AddOrUpdateDataTableEntries({
        sourceType: undefined /* STRING */,
        values: infoTable /* INFOTABLE */,
        location: undefined /* LOCATION */,
        source: undefined /* STRING */,
        tags: undefined /* TAGS */
    });
} catch(err) {
    logger.error("InsertBadItemFromCSV Error: " + err);
}

var result = "残量";
var now = new Date();

if (me.ResetDate === undefined) {
    result += "100%";
} else {
	var diffTime = now.getTime() - me.ResetDate.getTime();
    result += (diffTime>=me.TimeMax) ? "0%" : ((1-diffTime/me.TimeMax)*100).toFixed() + "%";
}
me.ProdPercentTitle = result;

pause(2000);

me.ErrorCodeTitle = "";
me.ErrorDetail = "";

if (me.UniCode && me.MotorLine) {
    var errorNote;
    let errInfo = Things["AR_POC_Database"].QueryGetLatestMachineLog({
        UnitCode: me.UnitCode /* STRING */,
        MotorLine: me.MotorLine /* STRING */
    });
    if (errInfo.getRowCount()>0 && errInfo.error_code) {
        me.ErrorCode = errInfo.error_code;
        let detail = Things["MEL.AR.ErrorMaster.DT"].GetDataTableEntryByKey({
            key: me.ErrorCode + me.MotorLine + me.UnitCode /* STRING */
        });
    	if (detail.getRowCount()>0) {
            errorNote = detail.ERROR_NOTE;
        } else {
            let detailDB = Things["AR_POC_Database"].QueryGetErrorMaster({
                UnitCode: me.UnitCode /* STRING */,
                MotorLine: me.MotorLine /* STRING */,
                ErrorCode: me.ErrorCode /* STRING */
            });
            if (detailDB.getRowCount()>0) errorNote = detailDB.error_note;
        }
        if (errorNote.indexOf(":")>0) {
            var errArr = errorNote.split(":"); 
    		me.ErrorCodeTitle = errArr[0];
            me.ErrorDetail = errArr[1];
        } else {
    		me.ErrorCodeTitle = errorNote;
        }
    }
}

